<?php       /** * @package WordPress * @since 1.0 */    get_header();$layout = apply_filters( 'yiw_layout_single_portfolio', yiw_layout_page() ); ?>                   <div id="content" class="layout-<?php echo $layout ?> group">				    <?php get_template_part( 'slogan' ) ?>            <!-- START CONTENT -->            <div id="primary" class="group">                <?php                     global $wp_query, $post, $yiw_portfolio;                    $tmp_query = $wp_query;                                        $post_type = yiw_get_portfolio_post_type();                    if ( !is_singular( $post_type ) )                        query_posts( 'post_type=' . $post_type . '&posts_per_page=1' );                    get_template_part( 'loop', 'portfolio' );                    $wp_query = $tmp_query;                           wp_reset_postdata();                ?>              </div>                                   <!-- END CONTENT -->            <!-- START LATEST NEWS -->            <?php if ( $layout != 'sidebar-no' ) get_sidebar('portfolio'); ?>            <!-- END LATEST NEWS -->           </div>      <?php get_footer() ?>